import React from 'react';
import { Grid, Box } from '@mui/material';
import PageContainer from 'src/components/container/PageContainer';
import './Article.css';
// components
// import SalesOverview from './components/SalesOverview';
// import YearlyBreakup from './components/YearlyBreakup';
// import RecentTransactions from './components/RecentTransactions';
// import ProductPerformance from './components/ProductPerformance';
// import Blog from './components/Blog';
// import MonthlyEarnings from './components/MonthlyEarnings';


const Article = () => {
  return (
    <PageContainer title="Article" description="this is Article">
      <Box>
        <div className="row">
          <Grid container spacing={3}>
            <h1><strong>Bài viết nổi bật</strong></h1>
          </Grid>
          <Grid container spacing={3}>
            <p>Tổng hợp các bài viết chia sẻ về kinh nghiệm tự học lập trình online và các kỹ thuật lập trình web.</p>
          </Grid>
          <div className="col">
            1 of 2




          </div>
          <div className="col">
            2 of 2



            
          </div>
        </div>
        <div className="featured-article">
          <div className="article-info">
            <div className="author-info">
              <img src="author-image-url.jpg" alt="Lý Cao Nguyên" className="author-image" />
              <span className="author-name">Lý Cao Nguyên</span>
            </div>
            <h2 className="article-title">Mình đã làm thế nào để hoàn thành một dự án website chỉ trong 15 ngày</h2>
            <p className="article-description">
              Xin chào mọi người mình là Lý Cao Nguyên, mình đã làm một dự án website front-end với hơn 100 bài học và 200 bài viết. Bài viết này...
            </p>
            <div className="article-meta">
              <span className="category-badge">Front-end</span>
              <span className="article-time">2 tháng trước</span>
              <span className="reading-time">4 phút đọc</span>
            </div>
          </div>
          <div classNameName="article-thumbnail">
            <img src="article-image-url.jpg" alt="Minh đã làm thế nào để hoàn thành một dự án website chỉ trong 15 ngày" />
          </div>
        </div>


      </Box>
    </PageContainer>
  );
};

export default Article;
