const StyledBox = styled(Box)(({ theme }) => ({
  backgroundColor: '#8000ff',
  borderRadius: '15px',
  padding: '20px',
  color: '#fff',
  textAlign: 'left',
  position: 'relative',
  overflow: 'hidden',
  minHeight: '200px',
}));

const DiscountText = styled(Typography)(({ theme }) => ({
  fontSize: '40px',
  fontWeight: 'bold',
  color: '#FFC0CB',
  textAlign: 'right',
  [theme.breakpoints.down('sm')]: {
    textAlign: 'center',
    fontSize: '30px',
  },
}));

const SubText = styled(Typography)(({ theme }) => ({
  fontSize: '16px',
  color: '#fff',
  marginBottom: '10px',
}));

const ActionButton = styled(Button)(({ theme }) => ({
  backgroundColor: '#ffffff',
  color: '#8000ff',
  borderRadius: '25px',
  padding: '10px 20px',
  fontSize: '16px',
  fontWeight: 'bold',
  marginTop: '20px',
  '&:hover': {
    backgroundColor: '#f0f0f0',
  },
  [theme.breakpoints.down('sm')]: {
    width: '100%',
  },
}));

const ImageBox = styled(Box)(({ theme }) => ({
  position: 'absolute',
  right: '-40px',
  bottom: '0',
  width: '250px',
  height: 'auto',
  opacity: 0.9,
  [theme.breakpoints.down('md')]: {
    position: 'static',
    marginTop: '20px',
    width: '100%',
  },
}));

const Home = () => {
  return (
    <PageContainer title="Dashboard" description="this is Dashboard">
      <Box>
        <Grid container spacing={3} sx={{ marginBottom: { xs: '50px', md: '50px' }, marginTop: '30px' }}>
          <Carousel variant="dark">
            <Carousel.Item>
              <StyledBox>
                <Grid container spacing={3} alignItems="center">
                  <Grid item xs={12} md={6}>
                    <Typography variant="h4" component="h2" fontWeight="bold">
                      Mở bán khóa JavaScript Pro
                    </Typography>
                    <SubText>
                      Từ 08/08/2024 khóa học sẽ có giá 1.399k (-200k nếu pre-order khóa HTML CSS Pro).
                      Khi full khóa học sẽ trở về giá gốc.
                    </SubText>
                    <ActionButton variant="contained">Học thử miễn phí</ActionButton>
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <DiscountText>1.199K</DiscountText>
                    <Typography variant="h6" style={{ textAlign: 'right', textDecoration: 'line-through', color: '#ccc' }}>
                      3.299K
                    </Typography>
                  </Grid>
                </Grid>
                <ImageBox>
                  <img src="/path-to-your-image1.png" alt="JavaScript Pro" style={{ width: '100%' }} />
                </ImageBox>
              </StyledBox>
            </Carousel.Item>

            <Carousel.Item>
              <StyledBox>
                <Grid container spacing={3} alignItems="center">
                  <Grid item xs={12} md={6}>
                    <Typography variant="h4" component="h2" fontWeight="bold">
                      Mở bán khóa HTML CSS Pro
                    </Typography>
                    <SubText>
                      Từ 08/08/2024 khóa học sẽ có giá 1.199k (-200k nếu pre-order khóa JavaScript Pro).
                      Khi full khóa học sẽ trở về giá gốc.
                    </SubText>
                    <ActionButton variant="contained">Học thử miễn phí</ActionButton>
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <DiscountText>999K</DiscountText>
                    <Typography variant="h6" style={{ textAlign: 'right', textDecoration: 'line-through', color: '#ccc' }}>
                      2.499K
                    </Typography>
                  </Grid>
                </Grid>
                <ImageBox>
                  <img src="/path-to-your-image2.png" alt="HTML CSS Pro" style={{ width: '100%' }} />
                </ImageBox>
              </StyledBox>
            </Carousel.Item>
          </Carousel>
        </Grid>

        <Grid container spacing={3} sx={{ marginBottom: { xs: '50px', md: '50px' }, marginTop: '30px' }}>
          <Grid item xs={12}>
            <Typography variant="h5" component="h2" fontWeight="bold" sx={{ textAlign: 'center' }}>
              Bài viết nổi bật
            </Typography>
          </Grid>
          {featuredPosts.map((post, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                <CardMedia
                  component="img"
                  height="140"
                  image={post.image}
                  alt={post.title}
                />
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography variant="h6" component="h2">
                    {post.title}
                  </Typography>
                  <Typography variant="body2" color="textSecondary">
                    {post.author} • {post.timeToRead}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </PageContainer>
  );
};

export default Home;
