import React, { useEffect, useState } from "react";
import { Box, Grid, Typography, IconButton, Avatar, Divider, CircularProgress } from '@mui/material';
import { IconHeart, IconMessageCircle } from '@tabler/icons';
//api
import { useNavigate, useParams } from 'react-router-dom';
import apis from "../../../apis/articleApi";
import userApi from "../../../apis/userApi";

const ArticleDetail = () => {
  const navigate = useNavigate();
  const { id } = useParams(); // Get the ID from the route parameters
  const [article, setArticle] = useState(null);
  const [loading, setLoading] = useState(true); // State for loading spinner
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState("");
  const [snackbarSeverity, setSnackbarSeverity] = useState("success");
  const [idMapping, setIdMapping] = useState({}); // State to hold the index-to-real-ID mapping
  const [users, setUsers] = useState([]);

   // Fetch all articles and set up ID mapping
   useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await apis.getList();
        if (response.status === 200) {
          const articles = response.data || [];
          const mapping = {};
          articles.forEach((article, index) => {
            mapping[index + 1] = article.id; // Map index + 1 to the actual article ID
          });
          setIdMapping(mapping);
        }
      } catch (error) {
        console.error("Error fetching articles:", error);
        setSnackbarMessage("Failed to fetch articles.");
        setSnackbarSeverity("error");
        setSnackbarOpen(true);
      }
    };

    fetchArticles();
  }, []);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await userApi.getList();
        if (response.status === 200) {
          const user = response.data || [];
          setUsers(user);
          console.log("Fetched users:", user);
        }
      } catch (error) {
        console.error("Error fetching users:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, []);

  // Fetch specific article details using the real ID
  useEffect(() => {
    const fetchArticleDetails = async () => {
      const realId = idMapping[id]; // Get the real ID from the mapping
      if (!realId) {
        setSnackbarMessage("Article not found.");
        setSnackbarSeverity("error");
        setSnackbarOpen(true);
        setLoading(false);
        return;
      }

      try {
        const response = await apis.getArticleDetails(realId);
        if (response.status === 200) {
          setArticle(response.data);
        }
      } catch (error) {
        console.error("Error fetching article details:", error);
        setSnackbarMessage("Failed to fetch article details.");
        setSnackbarSeverity("error");
        setSnackbarOpen(true);
      } finally {
        setLoading(false); // Stop loading spinner after data fetch
      }
    };

    if (Object.keys(idMapping).length > 0 && id) {
      fetchArticleDetails();
    }
  }, [id, idMapping]);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!article) {
    return (
      <Typography variant="h6" color="error" sx={{ textAlign: 'center', marginTop: '20px' }}>
        Article not found
      </Typography>
    );
  }

  return (
    <Box sx={{ padding: '20px', maxWidth: '1200px', margin: 'auto' }}>
      <Grid container spacing={3}>
        {/* Left Sidebar */}
        <Grid item xs={2} sx={{ textAlign: 'center' }}>
          <Avatar
            alt="Author Name"
            src="https://via.placeholder.com/150" // Replace with the author's avatar URL
            sx={{ width: 60, height: 60, margin: '0 auto' }}
          />
          <Typography variant="subtitle1" sx={{ marginTop: '10px' }}>
            {article.authorName || "Lý Cao Nguyên"}
          </Typography>
          <Typography variant="body2" color="textSecondary">
            Lập trình là đam mê
          </Typography>
          <Box sx={{ marginTop: '20px' }}>
            <IconButton aria-label="like">
              <IconHeart />
            </IconButton>
            <Typography variant="body2" sx={{ display: 'inline-block', marginLeft: '8px' }}>
              15
            </Typography>
            <IconButton aria-label="comments" sx={{ marginLeft: '16px' }}>
              <IconMessageCircle />
            </IconButton>
            <Typography variant="body2" sx={{ display: 'inline-block', marginLeft: '8px' }}>
              6
            </Typography>
          </Box>
        </Grid>

        {/* Main Content */}
        <Grid item xs={10}>
          <Typography variant="h3" sx={{ fontWeight: 'bold', marginBottom: '20px' }}>
            {article.title || "Mình đã làm thế nào để hoàn thành một dự án website chỉ trong 15 ngày"}
          </Typography>
          <Box sx={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
            <Avatar
              alt="Author Name"
              src="https://via.placeholder.com/150" // Replace with the author's avatar URL
              sx={{ width: 40, height: 40 }}
            />
            <Box sx={{ marginLeft: '10px' }}>
              <Typography variant="subtitle2">{article.authorName || "Lý Cao Nguyên"}</Typography>
              <Typography variant="body2" color="textSecondary">
                {article.publishedDate || "2 tháng trước · 4 phút đọc"}
              </Typography>
            </Box>
          </Box>

          <Divider sx={{ marginBottom: '20px' }} />

          <Typography variant="body1" paragraph>
            {article.intro || "Xin chào mọi người mình là Lý Cao Nguyên, mình đã làm một dự án website front-end với hơn 100 bài học và 200 bài viết."}
          </Typography>

          <Box sx={{ textAlign: 'center', marginBottom: '20px' }}>
            <img
              src={article.image || "https://via.placeholder.com/800x400"} // Replace with the article image URL
              alt="Article"
              style={{ width: '100%', borderRadius: '8px' }}
            />
          </Box>

          <Typography variant="body1" paragraph>
            {article.content || "Nội dung chi tiết của bài viết sẽ nằm ở đây. Bạn có thể thêm các đoạn văn bản, hình ảnh hoặc bất kỳ nội dung nào khác theo yêu cầu của bạn."}
          </Typography>
        </Grid>
      </Grid>
    </Box>
  );
};

export default ArticleDetail;
