import React from 'react';
import PageContainer from 'src/components/container/PageContainer';
import 'bootstrap/dist/css/bootstrap.min.css';
// components
// import SalesOverview from './components/SalesOverview';
// import YearlyBreakup from './components/YearlyBreakup';
// import RecentTransactions from './components/RecentTransactions';
// import ProductPerformance from './components/ProductPerformance';
// import Blog from './components/Blog';
// import MonthlyEarnings from './components/MonthlyEarnings';
import { Box, Button, Typography, Grid } from '@mui/material';
import { styled } from '@mui/system';

const StyledBox = styled(Box)(({ theme }) => ({
  backgroundColor: '#8000ff',
  borderRadius: '15px',
  padding: '20px',
  color: '#fff',
  textAlign: 'left',
  position: 'relative',
  overflow: 'hidden',
}));

const DiscountText = styled(Typography)(({ theme }) => ({
  fontSize: '40px',
  fontWeight: 'bold',
  color: '#FFC0CB',  // light pink color
  textAlign: 'right',
}));

const SubText = styled(Typography)(({ theme }) => ({
  fontSize: '16px',
  color: '#fff',
  marginBottom: '10px',
}));

const ActionButton = styled(Button)(({ theme }) => ({
  backgroundColor: '#ffffff',
  color: '#8000ff',
  borderRadius: '25px',
  padding: '10px 20px',
  fontSize: '16px',
  fontWeight: 'bold',
  marginTop: '20px',
  '&:hover': {
    backgroundColor: '#f0f0f0',
  },
}));

const ImageBox = styled(Box)(({ theme }) => ({
  position: 'absolute',
  right: '-40px',
  bottom: '0',
  width: '250px',
  height: 'auto',
  opacity: 0.9,
}));

const Home = () => {
  return (
    <PageContainer title="Dashboard" description="this is Dashboard">
      <Box>
        <Grid container spacing={3} sx={{ marginBottom: { xs: '50px', md: '50px' }, marginTop: '30px' }}>
        <StyledBox>
      <Grid container spacing={3} alignItems="center">
        <Grid item xs={12} md={6}>
          <Typography variant="h4" component="h2" fontWeight="bold">
            Mở bán khóa JavaScript Pro
          </Typography>
          <SubText>
            Từ 08/08/2024 khóa học sẽ có giá 1.399k (-200k nếu pre-order khóa HTML CSS Pro).
            Khi full khóa học sẽ trở về giá gốc.
          </SubText>
          <ActionButton variant="contained">Học thử miễn phí</ActionButton>
        </Grid>
        <Grid item xs={12} md={6}>
          <DiscountText>1.199K</DiscountText>
          <Typography variant="h6" style={{ textAlign: 'right', textDecoration: 'line-through', color: '#ccc' }}>
            3.299K
          </Typography>
        </Grid>
      </Grid>
      <ImageBox>
        <img src="/path-to-your-image.png" alt="JavaScript Pro" style={{ width: '100%' }} />
      </ImageBox>
    </StyledBox>
        </Grid>
      </Box>
    </PageContainer>
  );
};

export default Home;
