import React, { useEffect, useState } from 'react';
import { Box, Grid, Typography, IconButton, Avatar, Divider, CircularProgress } from '@mui/material';
import { IconHeart, IconMessageCircle } from '@tabler/icons';
import { useParams } from 'react-router-dom';
import apis from "../../../apis/articleApi";
import userApi from "../../../apis/userApi";
import Link from '@mui/material/Link';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import './style.css';

const ArticleDetail = () => {
  const { id } = useParams(); // Assuming the article ID comes from the route parameters
  const [article, setArticle] = useState(null);
  const [loading, setLoading] = useState(true);
  const [users, setUsers] = useState([]);
  useEffect(() => {
    const fetchArticle = async () => {
      try {
        const response = await apis.getArticleDetails(id);
        if (response.status === 200) {
          setArticle(response.data);
        }
      } catch (error) {
        console.error("Error fetching article:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchArticle();
  }, [id]);
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await userApi.getList();
        if (response.status === 200) {
          const user = response.data || [];
          setUsers(user);
          console.log("Fetched users:", user);
        }
      } catch (error) {
        console.error("Error fetching users:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, []);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!article) {
    return (
      <Typography variant="h5" color="text.secondary" align="center">
        Article not found
      </Typography>
    );
  }

  // Convert the created_at object to a readable date string
  const formattedDate = article.created_at ? new Date(article.created_at.seconds * 1000).toLocaleDateString() : '';

  return (
    <Box sx={{ padding: '20px', maxWidth: '1200px', margin: 'auto' }}>
      <Grid container spacing={3}>
        {/* Left Sidebar */}
        <Grid
          item
          xs={2}
          sx={{
            position: 'fixed', // Keeps the sidebar fixed relative to the viewport
            top: 0, // Position at the top of the viewport
            left: 0, // Position at the left of the viewport
            width: '16.6667%', // Adjust width to fit the xs={2} grid size
            height: '100vh', // Full viewport height
            backgroundColor: 'white', // Optional: to ensure it stays visible
            zIndex: 1, // Ensures it stays above other content
            overflowY: 'auto', // Allows scrolling inside the sidebar if content overflows
            padding: '16px', // Optional: for spacing inside the sidebar
          }}
        >
          <Typography variant="subtitle1" sx={{ marginTop: '10px' }}>
            {users?.filter(u => article?.user_id === u.id)?.[0]?.name}
          </Typography>
          <Typography variant="body2" color="textSecondary">
            L·∫≠p tr√¨nh l√† ƒëam m√™
          </Typography>
          <Divider sx={{ marginTop: '10px' }} />
          <Box sx={{ marginTop: '10px' }}>
            <IconButton aria-label="like">
              <IconHeart />
            </IconButton>
            <Typography variant="body2" sx={{ display: 'inline-block', marginLeft: '8px' }}>
              15
            </Typography>
            <IconButton aria-label="comments" sx={{ marginLeft: '16px' }}>
              <IconMessageCircle />
            </IconButton>
            <Typography variant="body2" sx={{ display: 'inline-block', marginLeft: '8px' }}>
              6
            </Typography>
          </Box>
        </Grid>

        {/* Main Content */}
        <Grid
    item
